INSERT INTO `wp_postmeta` VALUES (5291, 762, 'levi_stolpec', '<h2>WHAT DO WE DO?</h2>\r\n<img class="alignnone size-full wp-image-88" alt="imis" src="http://imis.eberce.si/wp-content/uploads/2013/09/imis.jpg" width="93" height="143" />\r\n\r\nDevelopment of an IMiS<sup>®</sup> software product for scanning, archiving, viewing, routing and searching for documents, and for linking applications.\r\n\r\nImplementation, integration and technical support for an IMiS<sup>®</sup>software product.\r\n\r\nDevelopment and implementation of enterprise solutions in the field of document management.\r\n\r\nAdvising and planning of document systems.\r\n\r\nAnaliza dokumentov in procesov.');
