INSERT INTO `wp_postmeta` VALUES (5293, 762, 'desni_stolpec', '<h2>WHY IMAGING SYSTEMS?</h2>\r\n<p class="side-box-1">We save our clients time and money.\r\n\r\nThe technical excellence of our solutions enables organized documentation and business processes.\r\n\r\nOver 260 companies and organizations in 45 countries across the world are using an IMiS<sup>®</sup>software product.\r\n\r\nThe highest number of operational systems in the field of Imaging &amp; Archiving in Slovenia.\r\n\r\nIMiS<sup>®</sup>/ARChive Server has been accredited by the Archives of the Republic of Slovenia.\r\n\r\nReliable and stable conducting of business (AAA, Bisnode 2011).</p>');
