INSERT INTO `wp_postmeta` VALUES (5599, 816, 'levi_stolpec', '<h2>KAJ DELAMO?</h2>\r\n<img class="alignnone size-full wp-image-88" alt="imis" src="http://imis.eberce.si/wp-content/uploads/2013/09/imis.jpg" width="93" height="143" />\r\n\r\nRazvoj programskega produkta IMiS®\r\nza skeniranje, arhiviranje, pregledovanje, razvrščanje in iskanje dokumentov,\r\nter povezovanje z aplikacijami.\r\n\r\nImplementacija, integracija in tehnična podpora za programski produkt IMiS®.\r\n\r\nSvetovanje, načrtovanje, razvoj\r\nin vzdrževanje dokumentnih sistemov.\r\n\r\nAnaliza dokumentov in procesov.');
INSERT INTO `wp_postmeta` VALUES (5600, 816, '_levi_stolpec', 'field_52361a54c9ac4');
INSERT INTO `wp_postmeta` VALUES (5601, 816, 'desni_stolpec', '<h2>ZAKAJ IMAGING SYSTEMS?</h2>\r\n<div class="side-box-1">\r\n\r\nPrihranimo vam čas in vaš denar.\r\n\r\nTehnična odličnost naših rešitev\r\nomogoča urejenost dokumentacije\r\nin poslovnih procesov.\r\n\r\n</div>\r\n<div class="side-box-2">\r\n\r\n260+ podjetij in organizacij\r\nv 45 državah sveta uporablja\r\nprogramski produkt IMiS®.\r\n\r\n</div>\r\n<div class="side-box-3">\r\n\r\nNajveč delujočih sistemov\r\ns področja »Imaging &amp; Archiving«\r\nv Sloveniji.\r\n\r\nIMiS/ARC je akreditiran kot infrastrukturna programska oprema pri Arhivu RS.\r\n\r\nNaša poslovna stabilnost zavaruje vašo investicijo v dokumentni sistem.\r\n\r\n</div>');
INSERT INTO `wp_postmeta` VALUES (5602, 816, '_desni_stolpec', 'field_523958eea4458');
INSERT INTO `wp_postmeta` VALUES (5603, 816, 'besedilo-1', '<h1>DOKUMENTNI SISTEMI</h1>\r\nElektronska podpora procesom, skeniranje,\r\nelektronsko podpisovanje,časovno žigosanje\r\narhiviranje, iskanje, pregledovanje,\r\nrevizijska sled, integracija z aplikacijami\r\n<div class="slider-panel-links"><a title="Dokumentni sistem" href="http://imis.eberce.si/resitve/dokumentni-sistemi/">INFORMACIJE</a> <a title="Primeri uporabe" href="http://imis.eberce.si/resitve/dokumentni-sistemi/primeri-uporabe/">PRIMERI UPORABE</a><a title="Vstopna" href="http://imis.eberce.si/">\r\n</a></div>');
INSERT INTO `wp_postmeta` VALUES (5604, 816, '_besedilo-1', 'field_523aac41000c9');
