INSERT INTO `wp_posts` VALUES (394, 1, '2013-10-14 14:12:04', '2013-09-26 15:51:25', '<div class="vstopna-box vstopna-box1">\r\n<p style="padding: 8px 15px 8px 15px; margin: 0;">Centralization of orders and complete control over the use of funds.</p>\r\n<p style="padding: 0; margin: 0;"><a style="font-size: 11px; border-top: 1px dotted #999; display: block; padding: 5px 15px 3px 15px;" title="The central procurement process" href="http://imis.eberce.si/en/solutions/document-management-systems/the-central-procurement-process/">&gt;&gt;&gt; MORE...</a></p>\r\n\r\n</div>\r\n<img class="alignnone size-full wp-image-116" alt="vstopna-slika1" src="http://imis.eberce.si/wp-content/uploads/2013/09/vstopna-slika1.jpg" width="174" height="119" />\r\n<div class="vstopna-box vstopna-box2">\r\n<p style="padding: 8px 15px 8px 15px; margin: 0;">Adapt banking processes to the demands of modern business operations.</p>\r\n<p style="padding: 0; margin: 0;"><a style="font-size: 11px; border-top: 1px dotted #999; display: block; padding: 5px 15px 3px 15px;" title="Banking transaction approval process" href="http://imis.eberce.si/en/solutions/document-management-systems/banking-transaction-approval-process/">&gt;&gt;&gt; MORE...</a></p>\r\n\r\n</div>\r\n<div class="vstopna-box vstopna-box3">\r\n<p style="padding: 8px 15px 8px 15px; margin: 0;">Leasing processes can be performed with greater speed and effectiveness electronically.</p>\r\n<p style="padding: 0; margin: 0;"><a style="font-size: 11px; border-top: 1px dotted #999; display: block; padding: 5px 15px 3px 15px;" title="Leasing transaction approval process" href="http://imis.eberce.si/en/solutions/document-management-systems/leasing-transaction-approval-process/">&gt;&gt;&gt; MORE...</a></p>\r\n\r\n</div>', 'Home', '', 'publish', 'open', 'open', '', 'entry', '', '', '2013-10-18 00:48:52', '2013-10-18 00:48:52', '', 0, 'http://imis.eberce.si/?page_id=394', 0, 'page', '', 0);
