INSERT INTO `wp_posts` VALUES (626, 1, '2009-07-10 20:04:50', '2009-07-10 20:04:50', '<blockquote><strong>Prihajajoča verzija 8 IMiS® Imaging klientov, arhivskega in OCR strežnika prinaša mnogo novosti in inovacij na področju uporabniškega vmesnika, na novo vključenih tehnologij in rešitev, ki prispevajo k enostavnosti uporabe in večji učinkovitosti.</strong></blockquote>\r\n<div>Velik poudarek je bil dan tudi spremembam na zalednem delu in programabilnem nivoju, ki omogočajo enostavno integracijo s praktično katerimkoli programskim produktom, ki potrebuje zanesljivo in varno platformo za arhiviranje in opcijsko za uporabljanje dokumentov.</div>', 'Kaj prinaša IMiS verzija 8?', '', 'publish', 'open', 'open', '', 'kaj-prinasa-imis-verzija-8', '', '', '2013-10-15 20:06:10', '2013-10-15 20:06:10', '', 200, 'http://imis.eberce.si/?page_id=626', 0, 'page', '', 0);
